// Menu

var show_menu = document.querySelector(".show_menu_btn"),
	viewport_width = document.documentElement.clientWidth,
	menu = document.querySelector("#nav_main ul");
	
600 >viewport_width && (menu.style.display = "none"), window.addEventListener("resize", function() {
	var e = document.documentElement.clientWidth;
	menu.style.display = e > 599?"block": "none"
}), show_menu.addEventListener("click", function() {
	var e = document.querySelector(show_menu.getAttribute("data-target"));
	"none" == e.style.display?(e.style.display = "block",
		show_menu.innerHTML = show_menu.getAttribute("data-shown-text")):(e.style.display = "none",
		show_menu.innerHTML = show_menu.getAttribute("data-hidden-text"))
});

